<script>
import Calendar from '@/lib/calendar'
export default {
  data () {
    return {
      calendardata: null
    }
  },

  filters: {
    filterdateShow (date) {
      if (date < 10) {
        return `0${date}`
      }
      return date
    }
  },

  created () {
    this.calendardata = new Calendar()
    this.calendardata.getMonthEveryList()
  },

  methods: {
    updateMonth (updateType) {
      this.calendardata.updateCurrentMonth(updateType)
    },

    updateYear (updateType) {
      this.calendardata.updateCurrentYear(updateType)
    }
  }
}
</script>

<style scoped lang="less">
